import React, { useState } from 'react';
import { Box, Typography, Paper } from '@mui/material';
import BasicTextFields from './inputs';
import MultipleSelect from './operation';
import BasicButtons from './submit';

function Body() {
  const [firstNumber, setFirstNumber] = useState('');
  const [secondNumber, setSecondNumber] = useState('');
  const [operation, setOperation] = useState('');
  const [result, setResult] = useState('');

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/calculate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstNumber,
          secondNumber,
          operation,
        }),
      });
  
      if (!response.ok) {
        const errorData = await response.json();
        console.error('API Error:', errorData); // Log backend error message
        setResult(`Error: ${errorData.error}`);
        return;
      }
  
      const data = await response.json();
      setResult(data.result);
    } catch (error) {
      console.error('Fetch Error:', error); // Log fetch error
      setResult('Error calculating result');
    }
  };
  

  return (
    <Box>
      <Paper
        sx={{
          p: 3,
          elevation: 3,
          backgroundColor: '#5d4037',
          color: '#ffffff',
          maxWidth: '600px',
        }}
      >
        <Typography variant="h4" gutterBottom>
          SIMPLE CALCULATOR
        </Typography>
        <Typography variant="body1">
          Enter the numbers and select the operation to perform the calculation.
        </Typography>
        <BasicTextFields
          firstNumber={firstNumber}
          secondNumber={secondNumber}
          setFirstNumber={setFirstNumber}
          setSecondNumber={setSecondNumber}
        />
        <MultipleSelect operation={operation} setOperation={setOperation} />
        <BasicButtons handleSubmit={handleSubmit} />
        <Typography variant="body1">Result is: {result}</Typography>
      </Paper>
    </Box>
  );
}

export default Body;
